properties.buttons_css:
/*Buttons CSS*/

 /*Webpart wrapper*/
 .btnmsf__block {
 display: flex;
 flex-direction: column;
 padding: 10px;
 border-radius: 60px;
 }

 .btnmsf__header {
 width: 100%;
 text-align: center;
 font-size: 20px }

 /*Input field*/
 .btnmsf__input_wrapper {
 width: 100%;
 display:flex;
 justify-content: center;
 margin: 10px 0;
 }

 .btnmsf__input {
 width: 80%;
 padding: 5px;
 border-radius: 10px;
 }

 /*Buttons wrapper*/
 .btnmsf__buttons {
 width: 100%;
 display: flex;
 justify-content: center;
 flex-wrap: wrap;
 gap: 40px;
 }

 /*Individual Button*/
 .btnmsf__button {
 width: 105px;
 height: 105px;
 border-radius: 105px;
 display: flex;
 flex-direction: column;
 align-items: center;
 justify-content: center;
 cursor: pointer;
 transition: 0.2s;
 box-shadow: 0 0 5px rgba(0,0,0,0.5);
 background: rgb(145,0,255);
 background: linear-gradient(266deg, rgba(145,0,255,1) 0%, rgba(87,78,255,1) 100%);
 }

 .btnmsf__button:hover {
 transform: scale(1.1) }

 .btnmsf__button_icon {
 font-size: 30px;
 padding: 2px;
 color: rgb(245, 243, 243);
 }

 .btnmsf__button_icon_custom {
 width: 100px;
 height: 100px;
 }

 .btnmsf__button_text {
 font-size: 17px;
 color: white;
 font-weight: 600;
 text-align: center;
 }

 /*Responsive design*/
 @media screen and (max-width: 1024px) {
 .btnmsf__buttons {
 display: grid;
 grid-template-columns: 1fr 1fr 1fr;
 }

 .btnmsf__button {
 width: 90px;
 height: 90px;
 border-radius: 90px;
 }

 .btnmsf__button_icon {
 font-size: 26px }

 .btnmsf__button_text {
 font-size: 16px;
 }

 }
