properties.generalCSS:
/*General visuals, you can deploy your full CSS solution here 
or use other editors. You can also set CSS variables here.*/

 :root {
 --lta_primary: #f1f1f1;
 --lta_secondary: #ff0000;
 --lta_tercial: #800080;
 }

.lta__wrapper {
 box-shadow: 0 0 5px rgba(0,0,0,0.5);
 border-radius: 15px;
 box-sizing: content-box;
 background-color: rgb(245, 245, 245);
 }

 .lta__header {
 display: flex;
 justify-content: space-between;
 align-items: center;
 }

 .lta__header h1 {
 font-size: 26px }

 .lta__input {
 width: 100%;
 border-radius: 30px;
 padding: 5px 10px;
 outline: none;
 transition: 0.2s;
 border: 1px solid #555;
 margin: 2px;
 box-sizing: border-box;
 }

 .lta__input:focus {
 outline: none;
 border: 1px solid #3a3a3a;
 }

properties.catfilterCSS:
/*Visuals for category filters and icons*/

 .lta__category_block {
 width: 100%;
 position: relative;
 display: flex;
 justify-content: space-evenly;
 align-items: center;
 flex-wrap: nowrap;
 list-style-type: none;
 cursor:  pointer !important;
 padding: 0 }

 .lta__category_button {
 display: flex;
 flex-direction: column;
 align-items: center;
 outline: none;
 width: 95px;
 height: 50px;
 border-radius: 50px;
 padding: 5px;
 border: none;
 background-color: white;
 cursor: pointer;
 flex-shrink: 0;
 transition: 0.2s;
 margin-bottom: 15px;
 color: #555 }

 .lta__category_button input[type='checkbox'] {
 -webkit-appearance: none;
 appearance: none;
 background-color: #fff;
 margin: 0;
 }

 .lta__category_button:hover {
 color: red;
 transform: scale(1.1);
 }

 .lta__category_button_selected {
 color: red;
 position: relative;
 }

 .lta__category_button_selected:before {
 content: '✓';
 position: absolute;
 top: 5px;
 right: 15px;
 font-size: 12px;
 width: 15px;
 height: 15px;
 border-radius: 15px;
 text-align: center;
 line-height: 15px;
 background-color: #555;
 color: white }

 .lta__category_icon_custom {
 font-size: 35px;
 height: 35px;
 margin-bottom: 5px;
 }

 .lta__category_icon {
 height: 35px;
 font-size: 35px;
 margin-bottom: 5px;
 }

 .lta__category_label {
 color: grey;
 font-size: 10px;
 text-align: center;
 }

properties.subcatfilterCSS:
/*Visuals for subcategory filters and icons*/

 .lta__subcategory_block { width: 100%;
 position: relative;
 display: flex;
 justify-content: start;
 align-items: center;
 flex-wrap: nowrap;
 list-style-type: none;
 cursor:  pointer !important;
 padding: 0;
 overflow-x: scroll;
 overflow-y: hidden;
 }

 .lta__subcategory_button { display: flex;
 flex-direction: column;
 align-items: center;
 outline: none;
 width: 85px;
 height: 40px;
 border-radius: 40px;
 padding: 3px;
 border: none;
 background-color: rgba(255, 255, 255, 0);
 cursor: pointer;
 flex-shrink: 0;
 transition: 0.2s;
 margin-bottom: 10px;
 color: #555 }

 .lta__subcategory_button input[type='checkbox'] { -webkit-appearance: none;
 appearance: none;
 background-color: #fff;
 margin: 0;
 }

 .lta__subcategory_button:hover { color: red;
 transform: scale(1.1);
 }

 .lta__subcategory_button_selected { color: red;
 position: relative;
 }

 .lta__subcategory_button_selected:before { content: '✓';
 position: absolute;
 top: 4px;
 right: 12px;
 font-size: 10px;
 width: 12px;
 height: 12px;
 border-radius: 12px;
 text-align: center;
 line-height: 12px;
 background-color: #555;
 color: white }

 .lta__subcategory_icon { height: 25px;
 font-size: 25px;
 margin-bottom: 5px;
 }

 .lta__subcategory_label { color: grey;
 font-size: 8px;
 text-align: center;
 }

properties.sortbyControlCSS:
/*Visuals for sort by control*/

 .lta__sortby_box {
 width: 100%;
 margin: 2px 0;
 display: flex;
 justify-content: start;
 position: relative;
 overflow-x: hidden;
 overflow-y: hidden;
 }

 .lta__sortby_button {
 width: 250px;
 text-align: start;
 color: var(--lta_secondary);
 background-color: white;
 height: 30px;
 border-radius: 30px;
 border: none;
 cursor: pointer;
 position: relative;
 z-index: 2;
 }

 .lta__sortby_choices {
 display: flex;
 justify-content: start;
 transition: 0.4s;
 width: 80%;
 position: relative;
 z-index: 1;
 }

 .lta__sortby_choice {
 width: 200px;
 border: 0;
 outline: 0;
 background-color:rgba(0,0,0,0);
 cursor: pointer }

 .lta__sortby_choice_selected {
 border-bottom: 1px solid var(--lta_secondary);
 }

