properties.groupbyCSS:
/*Visuals for group headers box*/

 .lta__groupbyCategory_wrapper {
 width: 100%;
 display: flex;
 flex-direction: column;
 align-items: center;
 margin-bottom: 10px;
 border-radius: 10px;
 box-shadow: 0 0 5px rgba(0,0,0,0.2);
 }

 .lta__groupbyCategory_wrapper_expanded {
 box-shadow: 0 0 10px rgba(0,0,0,0.5);
 }

 .lta__groupbyCategory_heading {
 font-size: 22px;
 border: 0;
 width: 100%;
 height: 50px;
 border-radius: 10px 10px 0 0;
 cursor: pointer;
 background-color: white;
 display: flex;
 justify-content: space-between;
 align-items: center;
 border-bottom: 1px solid red;
 box-shadow: 0 0 5px rgba(0,0,0,0.2);
 transition: 0.2s;
 }

 .lta__groupbyCategory_heading:hover {
 transform: scale(1.01) }

 /*Visuals for Subcategory group*/

 .lta__groupbySubcategory_wrapper {
 width: 95%;
 display: flex;
 flex-direction: column;
 align-items: end;
 margin: 5px;
 }

 .lta__groupbyCategory_wrapper_expanded {
 box-shadow: 0 0 10px rgba(0,0,0,0.5);
 }

 .lta__groupbySubcategory_heading {
 font-size: 18px;
 border: 0;
 width: 100%;
 height: 40px;
 border-radius: 10px;
 cursor: pointer;
 background: rgb(255,241,225);
 background: linear-gradient(90deg, rgba(255,241,225,1) 0%, rgba(252,244,228,1) 67%, rgba(251,230,230,0.15) 89%, rgba(255,255,255,0.1) 100%);
 display: flex;
 justify-content: space-between;
 align-items: center;
 transition: 0.2s;
 }

 .lta__groupbySubcategory_heading:hover {
 transform: scale(1.01) }

 /*Visuals for icons in group headers*/

 .lta__groupby_category_icon {
 width: 30px;
 height: 30px;
 border-radius: 30px;
 line-height: 30px;
 text-align: center;
 font-size: 25px;
 margin-left: 5px }

 .lta__groupby_subcategory_icon {
 width: 25px;
 height: 25px;
 border-radius: 25px;
 line-height: 25px;
 text-align: center;
 font-size: 20px;
 margin-left: 5px }

properties.cardCSS:
/*Visuals for content cards*/

 .lta__card_wrapper {
 width: 98%;
 box-sizing: border-box;
 padding: 10px;
 display: flex;
 flex-direction: column;
 border-radius: 15px;
 margin: 5px 10px;
 margin-bottom: 10px;
 overflow-x: hidden;
 overflow-y: auto;
 transition: 0.5s;
 background-color: rgb(250, 250, 250);
 }

 .lta__card_wrapper_opened {
 box-shadow: 0 0 5px rgba(0,0,0,0.3);
 }

 .lta__card_heading {
 outline: none;
 font-size: 18px;
 width: 100%;
 background-color: rgba(255,255,255,0);
 border: none;
 margin-top: 5px;
 cursor: pointer;
 display: flex;
 justify-content: space-between;
 align-items: center;
 transition: 0.3s;
 }

 .lta__card {
 margin-top: -148px;
 width: 850px;
 height: 85vh;
 box-sizing: border-box;
 padding: 10px 20px;
 display: flex;
 flex-direction: column;
 border-radius: 15px;
 box-shadow: 0 0 20px rgba(0,0,0,0.4);
 background-color: white;
 overflow: hidden;
 }

 .lta__card_title {
 color: red;
 font-size: 18px;
 margin: 0;
 }

 .lta__card_details_box {
 display: flex;
 width: 100%;
 box-shadow: 0 0 5px rgba(0,0,0,0.2);
 border-radius: 10px;
 padding: 10px;
 box-sizing: border-box;
 margin: 20px 0;
 }

 .lta__card_detail_label {
 font-size: 14px;
 font-weight: 700;
 margin-bottom: 5px;
 color: purple;
 }

 .lta__card_detail_group1_item {
 font-size: 12px;
 padding: 2px 5px;
 margin-right: 5px;
 border-radius: 14px;
 background-color: white;
 box-shadow: 0 0 4px rgba(0,0,0,0.3);
 }

 .lta__card_detail_group2_item {
 font-size: 14px;
 padding: 2px 5px;
 margin-right: 5px;
 border-radius: 14px;
 background-color: white;
 box-shadow: 0 0 4px rgba(117, 80, 252, 0.3);
 }

 .lta__card_detail_group3_item {
 font-size: 14px;
 padding: 2px 5px;
 margin-right: 5px;
 border-radius: 14px;
 background-color: white;
 box-shadow: 0 0 4px rgba(250, 82, 82, 0.3);
 }

 .lta__link {
 font-size: 28px;
 padding: 5px;
 border-radius: 100%;
 color: white;
 background-color:purple;
 transition: 0.3s;
 }

 .lta__link:hover {
 transform: rotate(180deg);
 }

 .lta__card_content_box_wrapper {
 display: flex;
 flex-direction: column;
 overflow-y: auto;
 overflow-x: hidden;
 }

 .lta__card_contentA_box {
 width: 100%;
 box-sizing: border-box;
 }

 .lta__card_contentB_box {
 width: 100%;
 box-sizing: border-box;
 }
