properties.welcome_css:
.wellmsf__wrapper {
  display: flex;
  justify-content: start;
  align-items: center;
  width: 100%;
}
.wellmsf__content {
  display: flex;
  flex-direction: column;
  padding: 10px;
  border-radius: 15px;
  width: 700px;
  background: rgb(200,0,0);
  background: linear-gradient(86deg, rgba(200,0,0,1) 0%, rgba(203,15,15,1) 22%, rgba(251,203,151,1) 62%, rgba(255,255,255,1) 100%)
}
.wellmsf__message {
  margin: 0;
  color: white;
  margin-bottom: 10px;
  font-size: 30px;
}
.wellmsf__message span:nth-child(1) {
  color: rgb(238, 248, 147)
}
.wellmsf__message span:nth-child(2) {
  color: #000000
}
.wellmsf__message span:nth-child(3) {
  color: #c80000
}
.wellmsf__paragraph {
  font-size: 18px;
  color: white;
  margin: 0;
}
.wellmsf__button_box {
  width: 100%;
  display: flex;
  justify-content: end;.wellmsf__button {
  width: 80px;
  height: 40px;
  border-radius: 5px;
  background-color: #c80000;
  transition: 0.2s;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
}
.wellmsf__button:hover {
  transform: scale(1.1)
}
.wellmsf__button_icon {
  color: white;
  font-size: 18px;
}
.wellmsf__button_text {
  font-size: 16px;
  color: white;
  font-weight: 600;
}
.wellmsf__button_icon_custom {
  display: none
}
