properties.welcome_css:
.wellmsf__wrapper {
 display: flex;
 justify-content: start;
 align-items: center;
 width: 100%;
 }

 .wellmsf__content {
 display: flex;
 flex-direction: column;
 justify-content: center;
 padding: 25px 15px;
 width: 700px;
 height: 200px;
 border-radius: 15px;
 border: 2px solid red;
 box-shadow: 0 0 5px rgba(0,0,0,0.2);
 }

 .wellmsf__message {
 margin: 0;
 color: red;
 margin-bottom: 10px;
 font-size: 30px;
 }

 .wellmsf__message span:nth-child(1) {
 color: orangered }

 .wellmsf__message span:nth-child(2) {
 color: #000000 }

 .wellmsf__message span:nth-child(3) {
 font-size: 50px;
 color: red;
 display: inline-block;
 animation-name: example;
 animation-duration: 4s;
 animation-timing-function: ease;
 animation-iteration-count: infinite;
 transform-origin: bottom;
 }

 .wellmsf__paragraph {
 font-size: 16px;
 color: red;
 margin: 0;
 width: 500px;
 }

 .wellmsf__button_box {
 width: 100%;
 display: flex;
 justify-content: end;
 align-items: center;
 }

 .wellmsf__button {
 width: 80px;
 height: 80px;
 border-radius: 80px;
 margin: 5px;
 background: rgb(200,0,0);
 background: linear-gradient(86deg, rgba(200,0,0,1) 0%, rgba(203,15,15,1) 59%, rgba(255,81,0,1) 85%);
 transition: 0.2s;
 display: flex;
 flex-direction: column;
 justify-content: center;
 align-items: center;
 cursor: pointer;
 box-shadow: 0 0 5px rgba(0,0,0,0.2);
 }

 .wellmsf__button:hover {
 transform: scale(1.1) }

 .wellmsf__button_icon {
 color: white;
 font-size: 25px;
 }

 .wellmsf__button_text {
 font-size: 14px;
 color: white;
 font-weight: 600;
 }

 .wellmsf__button_icon_custom {
 display: none }

 @keyframes example {
 0% {
 transform: rotate(0deg);
 }

 25% {
 transform: rotate(15deg);
 }

 75% {
 transform: rotate(-15deg);
 }

 100% {
 transform: rotate(0deg);
 }

 }
